mapper.update:
UPDATE reply SET
content = #{content} 
WHERE id = #{id}

		INSERT IGNORE INTO reply_likes (reply_id, user_id)
        VALUES (#{replyId}, #{userId});

        UPDATE reply
        SET like = (
            SELECT COUNT(*)
            FROM reply_likes
            WHERE reply_id = #{replyId}
        )
        WHERE id = #{replyId};

		DELETE FROM reply_likes WHERE reply_id = #{replyId} AND user_id = #{userId};

		UPDATE reply
        SET like = (
            SELECT COUNT(*)
            FROM reply_likes
            WHERE reply_id = #{replyId}
        )
        WHERE id = #{replyId};
