mapper.update:
UPDATE board SET 
viewCnt = viewCnt+1
WHERE id=#{id}

		INSERT IGNORE INTO board_likes (board_id, user_id)
        VALUES (#{boardId}, #{userId});

        UPDATE board
        SET like = (
            SELECT COUNT(*)
            FROM board_likes
            WHERE board_id = #{boardId}
        )
        WHERE id = #{boardId};

		DELETE FROM board_likes WHERE board_id = #{boardId} AND user_id = #{userId};

		UPDATE board
        SET like = (
            SELECT COUNT(*)
            FROM board_likes
            WHERE board_id = #{boardId}
        )
        WHERE id = #{boardId};

mapper.select:
SELECT * FROM board 

	WHERE ${key} LIKE CONCAT('%',#{word},'%')


	ORDER BY ${orderBy} ${orderByDir}
